The following example shows how to create a  in C# and add nodes and connections.

             using System;
             using System.Collections.Generic;
             using System.Linq;
             using System.Text;
             using System.Windows;
             using System.Windows.Controls;
             using System.Windows.Data;
             using System.Windows.Documents;
             using System.Windows.Input;
             using System.Windows.Media;
             using System.Windows.Media.Imaging;
             using System.Windows.Navigation;
             using System.Windows.Shapes;
             using System.ComponentModel;
             using Syncfusion.Core;
             using Syncfusion.Windows.Diagram;
            namespace SilverlightApplication1
             {
             public partial class MainPage : UserControl
             {
                public DiagramControl Control;
                public DiagramModel Model;
                public DiagramView View;
                public MainPage()
                {
                   InitializeComponent ();
                   Control = new DiagramControl ();
                   Model = new DiagramModel ();
                   View = new DiagramView ();
                   Control.View = View;
                   Control.Model = Model;
                   View.Bounds = new Thickness(0, 0, 1000, 1000);
                   //Specifies the node
                    Node n = new Node(Guid.NewGuid(), "Start");
                    n.Shape = Shapes.FlowChart_Start;
                    n.IsLabelEditable = true;
                    n.Label = "Start";
                    n.Level = 1;
                    n.OffsetX = 150;
                    n.OffsetY = 25;
                    n.Width = 150;
                    n.Height = 75;
                    n.ToolTip="Start Node";
                    Model.Nodes.Add(n);
                    ConnectionPort port = new ConnectionPort();
                    port.Node=n;
                    port.Left=75;
                    port.Top=10;
                    port.PortShape = PortShapes.Arrow;
                    port.PortStyle.Fill = Brushes.Transparent;
                    port.Height = 11;
                    port.Width = 11;
                    n.Ports.Add(port);
                     Node n1 = new Node(Guid.NewGuid(), "Decision1");
                     n1.Shape = Shapes.FlowChart_Process;
                     n1.IsLabelEditable = true;
                     n1.Label = "Alarm Rings";
                     n1.Level = 2;
                     n1.OffsetX = 150;
                     n1.OffsetY = 125;
                     n1.Width = 150;
                     n1.Height = 75;
                    Model.Nodes.Add(n1);
                    ConnectionPort port1 = new ConnectionPort();
                    port1.Node=n;
                    port1.Left=75;
                    port1.Top=50;
                    port1.PortShape = PortShapes.Arrow;
                    port1.PortStyle.Fill = Brushes.Transparent;
                    port1.Height = 11;
                    port1.Width = 11;
                    n1.Ports.Add(port1);
                     LineConnector o = new LineConnector();
                     o.ConnectorType = ConnectorType.Straight;
                     o.TailNode = n1;
                     o.HeadNode = n;
                     o.LabelHorizontalAlignment = HorizontalAlignment.Center;
                     o.LabelVerticalAlignment = HorizontalAlignment.Center;
                     o.Label="Syncfusion";
                     o.ConnectionHeadPort = port;
                     o.ConnectionTailPort = port1;
                     o.HeadDecoratorShape=DecoratorShape.Arrow;
                     o.TailDecoratorShape=DecoratorShape.Arrow;
                     Model.Connections.Add(o);
                }
                }
                }

example.code:
 using Syncfusion.Windows.Diagram;
namespace SilverlightApplication1
 {
 public partial class MainPage : UserControl
 {
 public DiagramControl Control;
 public DiagramModel Model;
 public DiagramView View;
 public MainPage()
 {
 InitializeComponent ();
 Control = new DiagramControl ();
 Model = new DiagramModel ();
 View = new DiagramView ();
 Control.View = View;
 Control.Model = Model;
 View.Bounds = new Thickness(0, 0, 1000, 1000);
 //Creating node
 Node n = new Node(Guid.NewGuid(), "Start");
 n.Shape = Shapes.FlowChart_Start;
 n.IsLabelEditable = true;
 n.Label = "Start";
 n.Level = 1;
 n.OffsetX = 150;
 n.OffsetY = 25;
 n.Width = 150;
 n.Height = 75;
 n.ToolTip="Start Node";
 Model.Nodes.Add(n);
 Node n1 = new Node(Guid.NewGuid(), "Decision1");
 n1.Shape = Shapes.FlowChart_Process;
 n1.IsLabelEditable = true;
 n1.Label = "Alarm Rings";
 n1.Level = 2;
 n1.OffsetX = 150;
 n1.OffsetY = 125;
 n1.Width = 150;
 n1.Height = 75;
 Model.Nodes.Add(n1);
 //Creating a connection.
 LineConnector o2 = new LineConnector();
 o2.ConnectorType = ConnectorType.Straight;
 o2.TailNode = n1;
 o2.HeadNode = n;
 IShape node = o2.AdjacentNode(n1);
 Model.Connections.Add(o2);
 }
 }
 }

 using Syncfusion.Windows.Diagram;
namespace SilverlightApplication1
 {
 public partial class MainPage : UserControl
 {
 public DiagramControl Control;
 public DiagramModel Model;
 public DiagramView View;
 public MainPage()
 {
 InitializeComponent ();
 Control = new DiagramControl ();
 Model = new DiagramModel ();
 View = new DiagramView ();
 Control.View = View;
 Control.Model = Model;
 View.Bounds = new Thickness(0, 0, 1000, 1000);
 //Creating node
 Node n = new Node(Guid.NewGuid(), "Start");
 n.Shape = Shapes.FlowChart_Start;
 n.IsLabelEditable = true;
 n.Label = "Start";
 n.Level = 1;
 n.OffsetX = 150;
 n.OffsetY = 25;
 n.Width = 150;
 n.Height = 75;
 n.ToolTip="Start Node";
 Model.Nodes.Add(n);
 //Adding a port to the node
 ConnectionPort port = new ConnectionPort();
 port.Node=n;
 port.Left=75;
 port.Top=10;
 port.PortShape = PortShapes.Arrow;
 port.PortStyle.Fill = Brushes.Transparent;
 port.Height = 11;
 port.Width = 11;
 n.Ports.Add(port);
 Node n1 = new Node(Guid.NewGuid(), "Decision1");
 n1.Shape = Shapes.FlowChart_Process;
 n1.IsLabelEditable = true;
 n1.Label = "Alarm Rings";
 n1.Level = 2;
 n1.OffsetX = 150;
 n1.OffsetY = 125;
 n1.Width = 150;
 n1.Height = 75;
 Model.Nodes.Add(n1);
 ConnectionPort port1 = new ConnectionPort();
 port1.Node=n;
 port1.Left=75;
 port1.Top=50;
 port1.PortShape = PortShapes.Arrow;
 port1.PortStyle.Fill = Brushes.Transparent;
 port1.Height = 11;
 port1.Width = 11;
 n1.Ports.Add(port1);
 //Creating a connection.
 LineConnector o2 = new LineConnector();
 o2.ConnectorType = ConnectorType.Straight;
 o2.TailNode = n1;
 o2.HeadNode = n;
 o2.LabelHorizontalAlignment = HorizontalAlignment.Center;
 //Specifying the port to connect to.
 o2.ConnectionHeadPort = port;
 o2.ConnectionTailPort = port1;
 o2.HeadDecoratorShape=DecoratorShape.Arrow;
 o2.TailDecoratorShape=DecoratorShape.Arrow;
 Model.Connections.Add(o2);
 }
 }
 }

