using Syncfusion.Core;
using Syncfusion.Windows.Diagram;
namespace WpfApplication1
{
public partial class Window1 : Window
{
public DiagramControl Control;
public DiagramModel Model;
public DiagramView View;
public Window1 ()
{
InitializeComponent ();
Control = new DiagramControl ();
Model = new DiagramModel ();
View = new DiagramView ();
Control.View = View;
Control.Model = Model;
View.Bounds = new Thickness(0, 0, 1000, 1000);
Node n = new Node(Guid.NewGuid(), "Start");
n.Shape = Shapes.FlowChart_Start;
n.Level = 1;
n.OffsetX = 150;
n.OffsetY = 25;
n.Width = 150;
n.Height = 75;
Node n1 = new Node(Guid.NewGuid(), "End");
n1.Shape = Shapes.FlowChart_Start;
n1.Level = 1;
n1.OffsetX = 350;
n1.OffsetY = 325;
n1.Width = 100;
n1.Height = 75;
Model.Nodes.Add(n);
Model.Nodes.Add(n1);
Group g = new Group(Guid.NewGuid(), "group1");
g.AddChild(n);
g.AddChild(n1);
diagramModel.Nodes.Add(g);
}
}
}

using Syncfusion.Core;
using Syncfusion.Windows.Diagram;
namespace WpfApplication1
{
public partial class Window1 : Window
{
   public DiagramControl Control;
   public DiagramModel Model;
   public DiagramView View;
   public Window1 ()
   {
      InitializeComponent ();
      Control = new DiagramControl ();
      Model = new DiagramModel ();
      View = new DiagramView ();
      Control.View = View;
      Control.Model = Model;
      View.Bounds = new Thickness(0, 0, 1000, 1000);
      Node n = new Node(Guid.NewGuid(), "Start");
       n.Shape = Shapes.FlowChart_Start;
       n.Level = 1;
       n.OffsetX = 150;
       n.OffsetY = 25;
       n.Width = 150;
       n.Height = 75;
       Node n1 = new Node(Guid.NewGuid(), "End");
       n1.Shape = Shapes.FlowChart_Start;
       n1.Level = 1;
       n1.OffsetX = 350;
       n1.OffsetY = 325;
       n1.Width = 100;
       n1.Height = 75;
       Model.Nodes.Add(n);
       Model.Nodes.Add(n1);
       Group g = new Group(Guid.NewGuid(), "group1");
       g.AddChild(n);
       g.AddChild(n1);
       diagramModel.Nodes.Add(g);
       g.RemoveChild(n);
   }
   }
   }

